import React, { useState } from 'react';
import axios from 'axios';
import {
    Container,
    Navbar,
    Nav
} from 'react-bootstrap/';


const [name, setName] = useState('')
const [erro, setErro] = useState(false);

function handlePesquisa() {
    axios.get(`https://pokeapi.co/api/v2/pokemon/${name}`)
        .then(response => {
            const pesquisaPokemons = response.data;
            const pokemonsName = [];
            pesquisaPokemons.map(poke => {
                pokemonsName.push(poke.name);
            });
            localStorage.setItem('Pokemon Name: ', JSON.stringify(pokemonsName));
            setErro(false);
        })
        .catch(err => {
            setErro(true)
        })
}

const Header = () => (
    <header className="Pokemon-header">
        <Container fluid>
            <Navbar id="navbar" variant="dark">
                <Navbar.Brand href="#"> Pokémon </Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="mr-auto">
                        <Nav.Link href="#">GitHub</Nav.Link>
                        <Nav.Link href="#">Linkedin</Nav.Link>
                    </Nav>
                </Navbar.Collapse>
            </Navbar>
        </Container>
    </header>
);

export default Header;
