import React, { Component } from 'react';
import {
    Container,
    Row,
    Col,
    Form,
    FormControl,
    Button
} from 'react-bootstrap/';

class Body extends Component {
    constructor() {
        super();
        this.state = {
            pokemon: []
        };
    }
    componentDidMount() {
        fetch('https://api.reinaldowft.com/pokestore?page=1&limit=150')
            .then(response => response.json())
            .then(data => this.setState({ pokemon: data.results }))
    }

    render() {
        const { pokemon } = this.state;
        return (
            <div>
                <Container fluid>
                    <Row>
                        <Col></Col>
                    </Row>
                </Container>
            </div>
        )
    }
}

export default Body;
