import React, { useState, useEffect } from 'react';
import {
    Container,
    Row,
    Col,
    Card,
    Button,
    Modal
} from 'react-bootstrap/';


function Body() {
    const [pokemons, setPokemons] = useState([])
    useEffect(() => {
        fetch('https://api.reinaldowft.com/pokestore?page=1&limit=150')
            .then(response => response.json())
            .then(data => setPokemons(data.results))
    }, [])

    return (
        <div>
            <br />
            <Container className="bodyPokemon">
                <Row>
                    <Col></Col>
                    {pokemons.map(pokemon => (
                        <Col key={pokemon.id} md={4}>
                            <Card className="cardPokemon" border="danger">
                                <Card.Img src={pokemon.sprite} />
                                <Card.Body>
                                    <Card.Header>{pokemon.name}</Card.Header>
                                    {pokemon.types.map(type => {
                                        return <Card.Text> Tipo: {type.name}</Card.Text>
                                    })}
                                    <Card.Text> R$ {pokemon.price},00</Card.Text>
                                    <Button variant="danger">COMPRAR</Button>
                                </Card.Body>
                            </Card>
                            <br />
                        </Col>
                    ))}
                </Row>
            </Container>
        </div >
    )
}


export default Body;
