import React, { useState, useEffect } from 'react';
import {
    Container,
    Row,
    Col,
    Card,
    Button,
    Modal
} from 'react-bootstrap/';

function Example() {
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <>
            <Button variant="primary" onClick={handleShow}>
                Launch demo modal
          </Button>

            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
              </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
              </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

function Body() {
    const [pokemons, setPokemons] = useState([])
    useEffect(() => {
        fetch('https://api.reinaldowft.com/pokestore?page=1&limit=150')
            .then(response => response.json())
            .then(data => setPokemons(data.results))
    }, [])

    return (
        <div>
            <br></br>
            <Container fluid>
                <Row>
                    {pokemons.map(pokemon => (
                        <Col key={pokemon.id} xs={6} md={3}>
                            <Card className="cardPokemon" >
                                <Card.Img src={pokemon.sprite} />
                                <Card.Body>
                                    <Card.Title>{pokemon.name}</Card.Title>
                                    <Card.Text> {pokemon.desc} </Card.Text>
                                    {pokemon.types.map(type => {
                                        return <Card.Text> Tipo: {type.name}</Card.Text>
                                    })}
                                    <Card.Text> R$ {pokemon.price},00</Card.Text>

                                    <Button variant="danger">COMPRAR</Button>
                                </Card.Body>
                            </Card>
                        </Col>
                    ))}
                    <Col> <h1>teste</h1></Col>
                </Row>
            </Container>
        </div >
    )
}


export default Body;
