import React, { useState } from 'react';
import axios from 'axios';
import {
    Container,
    Row,
    Col,
    Form,
    FormControl,
    Button
} from 'react-bootstrap/';

function Search(props) {
    const [name, setName] = useState('')
    const [erro, setErro] = useState(false);




    function handlePesquisa() {
        axios.get(`https://pokeapi.co/api/v2/pokemon/${name}`)
            .then(response => {
                const pesquisaPokemons = response.data;
                const pokemonsName = [];
                pesquisaPokemons.map(poke => {
                    pokemonsName.push(poke.name);
                });
                localStorage.setItem('Pokemon Name: ', JSON.stringify(pokemonsName));
                setErro(false);
            })
            .catch(err => {
                setErro(true)
            })
    }

    return (
        <Container fluid ><hr></hr>
            <Row className="justify-content-md-center">
                <Col md="auto">
                    <Form inline>
                        <FormControl type="text" placeholder="Digite o Pokemon" className="mr-sm-2" value={name} onChange={e => setName(e.target.value)} />
                        <Button variant="outline-success" onClick={handlePesquisa}>Search</Button>
                    </Form>
                </Col>
            </Row>
        </Container>
    );

}




export default Search;
